import React, { useState } from 'react';
import Container from 'path-to-your-container-component'; // Replace with the actual path

// Assuming the 'cards' array is defined somewhere
const cards = [
  // ... Your card objects with 'id', 'image', 'topic', and 'text' properties
];

export default function News() {
  const [expandedCardId, setExpandedCardId] = useState(null);

  const handleKnowMoreClick = (id) => {
    setExpandedCardId((prevId) => (prevId === id ? null : id));
  };

  return (
    <section className="my-14">
      <Container>
        <div>
          {/* SectionTitle component should be defined */}
          {/* Import it or define it where appropriate */}
          <Section title="latest funding news" classes="text-center" />

          <div className="grid xl:grid-cols-4 lg:grid-cols-3 md:grid-cols-2 grid-cols-1 items-start gap-4 mt-8">
            {cards.map((card) => (
              <div
                className={`h-auto w-full md:w-[48%] lg:w-[31%] mx-auto px-2 pt-2 pb-4 rounded-md border-2 border-gray-200 relative z-10 transition-all ${
                  expandedCardId === card.id ? "h-auto" : "h-[320px]"
                }`}
                key={card.id}
              >
                <div className="relative rounded-xl overflow-hidden">
                  <img src={card.image} alt={`fund${card.id}`} className="w-full h-48 object-cover" />
                </div>
                <article className="px-2">
                  <h2
                    className="font-bold text-[18px] lg:text-[25px] hover:text-[#6D9886] transition-colors cursor-pointer my-4"
                    onClick={() => handleKnowMoreClick(card.id)}
                  >
                    {card.topic}
                  </h2>
                  <p className="font-light text-[14px]">
                    {expandedCardId === card.id ? card.text : card.text.slice(0, 100) + '...'}
                  </p>
                  <button
                    className="block mx-auto text-[#6D9886] mt-6"
                    onClick={() => handleKnowMoreClick(card.id)}
                  >
                    {expandedCardId === card.id ? 'Read less' : 'Know more'}
                  </button>
                </article>
              </div>
            ))}
          </div>

          <div className="text-xl text-center more_news_gradient h-44 w-full text-black font-bold uppercase py-4 flex items-end justify-center relative z-20">
            <a className="cursor-pointer">More news</a>
          </div>
        </div>
      </Container>
    </section>
  );
}
