import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { logo } from "../assets/home";
import Container from "./Container";

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobileView, setIsMobileView] = useState(false);

  const columnLinks = [
    { name: "First", link: "/first-link" },
    { name: "Second", link: "/second-link" },
    { name: "Third", link: "/third-link" },
    { name: "Fourth", link: "/fourth-link" },
    { name: "Fifth", link: "/fifth-link" },
    { name: "Sixth", link: "/sixth-link" },
    { name: "Seventh", link: "/seventh-link" },
    { name: "Eighth", link: "/eighth-link" },
  ];

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 768); // Adjust the threshold as needed
    };

    handleResize(); // Set initial view
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <div>
      <nav className="py-5 z-40 relative">
        <Container>
          <div className="flex items-center justify-between h-16">
            <div className="flex gap-4 items-center">
              <img className="h-[80px] w-[300px]" src={logo} alt="Workflow" />
              <div className="hidden md:block">
                {/* Additional content if needed */}
              </div>
            </div>

            <div className="flex gap-4 items-center">
              <Link to="/">
                <div className="hidden md:block hover:bg-button-primary px-4 py-1 rounded-xl">
                  Log In
                </div>
              </Link>
              <Link to="/">
                <div className="hidden md:block hover:bg-button-primary px-4 py-1 rounded-xl">
                  Sign Up
                </div>
              </Link>
            </div>

            <div className="-mr-2 flex md:hidden">
              <button
                onClick={() => setIsOpen(!isOpen)}
                type="button"
                className="bg-gray-800 inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-white"
                aria-controls="mobile-menu"
                aria-expanded="false"
              >
                <span className="sr-only">Open main menu</span>
                <i className="fas fa-bars"></i>
              </button>
            </div>
          </div>

          {isMobileView ? (
            isOpen && (
              <div className="md:hidden transition-all" id="mobile-menu">
                <div className="bg-[#A50014] h-10 flex items-center text-white">
                  {columnLinks.map((column, index) => (
                    <React.Fragment key={index}>
                      {index > 0 && <div className="h-full w-0.5 bg-white"></div>}
                      <Link to={column.link} className="flex-1 text-center">
                        {column.name}
                      </Link>
                    </React.Fragment>
                  ))}
                </div>
                <div className="flex gap-4 items-center mt-2">
                  <Link to="/">
                    <div className="hover:bg-button-primary px-4 py-1 rounded-xl">
                      Log In
                    </div>
                  </Link>
                  <Link to="/">
                    <div className="hover:bg-button-primary px-4 py-1 rounded-xl">
                      Sign Up
                    </div>
                  </Link>
                </div>
              </div>
            )
          ) : (
            <div className="md:hidden transition-all" id="mobile-menu">
              {/* Additional content for larger screens if needed */}
            </div>
          )}
        </Container>
      </nav>

      {/* Red color bar with centered columns separated by a white line */}
      <div className={`bg-[#A50014] h-10 flex items-center text-white ${isMobileView ? 'hidden' : 'block'}`}>
        {columnLinks.map((column, index) => (
          <React.Fragment key={index}>
            {index > 0 && <div className="h-full w-0.5 bg-white"></div>}
            <Link to={column.link} className="flex-1 text-center">
              {column.name}
            </Link>
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}

export default Navbar;
