import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { logo } from "../assets/home";
import Container from "./Container";

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobileView, setIsMobileView] = useState(false);
  const [announcements, setAnnouncements] = useState([
    "Announcement 1",
    "Announcement 2",
    "Announcement 3",
  ]);

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 768); // Adjust the threshold as needed
    };

    handleResize(); // Set initial view
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <div>
      {/* Announcements */}
      <div className="bg-gray-900 text-white py-2 text-center">
        <marquee>{announcements.join(" | ")}</marquee>
      </div>

      {/* Notifications */}
      <div className="bg-[#A50014] text-white py-2 text-center">
        {/* Your scrolling notifications content goes here */}
        <marquee>
          <span>Notification 1 | </span>
          <span>Notification 2 | </span>
          <span>Notification 3 | </span>
        </marquee>
      </div>

      {/* Navbar */}
      <nav className="py-5 z-40 relative">
        <Container>
          {/* ... rest of your existing code ... */}
        </Container>
      </nav>

      {/* Red color bar with centered columns separated by a white line */}
      <div className={`bg-[#A50014] h-10 flex items-center text-white ${isMobileView ? 'hidden' : 'block'}`}>
        {columnLinks.map((column, index) => (
          <React.Fragment key={index}>
            {index > 0 && <div className="h-full w-0.5 bg-white"></div>}
            <Link to={column.link} className="flex-1 text-center">
              {column.name}
            </Link>
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}

export default Navbar;
