import React, { useState } from "react";
import { news_2, news_3, news_4, news_5 } from "../../assets/home";
import Container from "../Container";
import SectionTitle from "./SectionTitle";

const cards = [
  {
    id: 1,
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Et venenatis pharetra enim est lectus. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Et venenatis pharetra enim est lectus. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Et venenatis pharetra enim est lectus. ",
    topic:
      "Roles of Mentor",
    image: news_2,
  },
  {
    id: 2,
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Et venenatis pharetra enim est lectus. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Et venenatis pharetra enim est lectus.",
    topic: "Development of research in the field of air pollution ",
    image: news_3,
  },
  {
    id: 3,
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Et venenatis pharetra enim est lectus. ",
    topic:
      "Development of research in the field of air pollution Development of research",
    image: news_4,
  },
  {
    id: 4,
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Et venenatis pharetra enim est lectus. ",
    topic: "Program life us expanding its offers for call for proposals",
    image: news_5,
  },
];

export default function News() {
  const [expandedCard, setExpandedCard] = useState(null);

  const handleReadMoreClick = (id) => {
    setExpandedCard(id === expandedCard ? null : id);
  };

  const containerStyle = expandedCard ? "h-auto" : "h-[400px]";

  return (
    <section className="my-20">
      <Container className={containerStyle}>
        <div>
          <SectionTitle title="student mentoring system" classes="text-center" />
          <p className="text-lg mb-8 mt-7 mb-10 text-center m-20" >
          Each faculty mentors a group of 20-25 students, with first-year students guided by faculty from basic sciences, Mathematics, and Humanities. From the second to fourth year, students have mentors from their parent department, maintaining a consistent mentoring relationship until graduation.
          </p>
          <div className="grid xl:grid-cols-4 lg:grid-cols-3 md:grid-cols-2 grid-cols-1 items-start gap-4 mt-8">
            {cards.map((card) => (
              <div
                className={`h-[600px] w-[320px] mx-auto px-2 pt-2 pb-4 rounded-md border-2 border-gray-200 relative z-10 ${
                  expandedCard === card.id ? "h-auto" : "h-[400px]"
                }`}
                key={card.id}
              >
                <div className="relative rounded-xl overflow-hidden">
                  <img src={card.image} alt="fund1" />
                </div>
                <article className="px-2">
                  <h2 className="font-bold text-[25px] hover:text-[#6D9886] transition-colors cursor-pointer my-4">
                    {card.topic}
                  </h2>
                  <p className="font-light text-[14px]">
                    {expandedCard === card.id ? card.text : `${card.text.slice(0, 100)}...`}
                  </p>
                  <button
                    className="block mx-auto text-[#6D9886] mt-6"
                    onClick={() => handleReadMoreClick(card.id)}
                  >
                    Read more
                  </button>
                </article>
              </div>
            ))}
          </div>

        </div>
      </Container>
    </section>
  );
}
