import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const slidesData = [
  {
    id: 1,
    heading: 'Slide 1',
    info: 'This is information for slide 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    image: 'path/to/image1.jpg',
  },
  {
    id: 1,
    heading: 'Slide ',
    info: 'This is information for slide 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    image: '../Home/',
  },
  {
    id: 1,
    heading: 'Slide 1',
    info: 'This is information for slide 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    image: 'path/to/image1.jpg',
  },
  {
    id: 1,
    heading: 'Slide 1',
    info: 'This is information for slide 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    image: 'path/to/image1.jpg',
  },
  {
    id: 1,
    heading: 'Slide 1',
    info: 'This is information for slide 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    image: 'path/to/image1.jpg',
  },
  // Add similar objects for the remaining slides
  // ...
];

const SliderComponent = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <Slider {...settings}>
      {slidesData.map((slide) => (
        <div key={slide.id}>
          <img src={slide.image} alt={`Slide ${slide.id}`} />
          <h2>{slide.heading}</h2>
          <p>{slide.info}</p>
          {/* You can customize the content inside each slide here */}
        </div>
      ))}
    </Slider>
  );
};

export default SliderComponent;
