import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { logo } from "../assets/home";
import Container from "./Container";

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobileView, setIsMobileView] = useState(false);
  const [notifications, setNotifications] = useState([
    "Notification 1",
    "Notification 2",
    "Notification 3",
  ]);

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 768); // Adjust the threshold as needed
    };

    handleResize(); // Set initial view
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const columnLinks = [ // Added Announcements heading
    { name: "First", link: "/first-link" },
    { name: "Second", link: "/second-link" },
    { name: "Third", link: "/third-link" },
    { name: "Fourth", link: "/fourth-link" },
    { name: "Fifth", link: "/fifth-link" },
    { name: "Sixth", link: "/sixth-link" },
    { name: "Seventh", link: "/seventh-link" },
    { name: "Eighth", link: "/eighth-link" },
  ];

  return (
    <div>
      {/* Notifications */}
      <div className="bg-[#A50014] text-white py-2 text-center overflow-hidden">
        <div className="flex items-center justify-start">
          {/* Announcements Heading */}
          <div className="flex items-center pr-4">
            <span className="text-sm font-">Announcements</span>
          </div>

          {/* Notifications Marquee */}
          <marquee>
            {notifications.map((notification, index) => (
              <span key={index}>{notification} | </span>
            ))}
          </marquee>
        </div>
      </div>

      {/* Navbar */}
      <nav className="py-5 z-40 relative">
        <Container>
          <div className="flex items-center justify-between h-16">
            <div className="flex gap-4 items-center">
              <img
                className="h-[80px] w-[300px]"
                src={logo}
                alt="Workflow"
              />
              <div className="hidden md:block">
                {/* Additional content if needed */}
              </div>
            </div>

            <div className="flex gap-4 items-center">
              <Link to="/">
                <div className="hidden md:block hover:bg-button-primary px-4 py-1 rounded-xl">
                  Log In
                </div>
              </Link>
              <Link to="/">
                <div className="hidden md:block hover:bg-button-primary px-4 py-1 rounded-xl">
                  Sign Up
                </div>
              </Link>
            </div>
          </div>

          {isMobileView ? (
            isOpen && (
              <div className="md:hidden transition-all" id="mobile-menu">
                {/* Mobile navigation links */}
              </div>
            )
          ) : (
            <div className="md:hidden transition-all" id="mobile-menu">
              {/* Additional content for larger screens if needed */}
            </div>
          )}
        </Container>
      </nav>

      {/* Red color bar with centered columns separated by a white line */}
      <div
        className={`bg-[#A50014] h-10 flex items-center text-white ${
          isMobileView ? "hidden" : "block"
        }`}
      >
        {columnLinks.map((column, index) => (
          <React.Fragment key={index}>
            {index > 0 && (
              <div className="h-full w-0.5 bg-white"></div>
            )}
            <Link to={column.link} className="flex-1 text-center">
              {column.name}
            </Link>
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}

export default Navbar;
