import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { discover_1, discover_2, discover_3 } from "../../assets/home";
import Container from "../Container";
import SectionTitle from "./SectionTitle";

const cards = [
  {
    id: 1,
    image: discover_1,
    title: "Grants",
  },
  {
    id: 2,
    image: discover_2,
    title: "Consortium",
  },
  {
    id: 3,
    image: discover_3,
    title: "Partners",
  },
  {
    id: 4,
    image: discover_3,
    title: "More Partners",
  },
];

const DiscoverCard = ({ card }) => {
  return (
    <div className="relative rounded-xl overflow-hidden cursor-pointer hover:scale-105 transition-transform">
      <img src={card.image} alt="discover_image" />
      <div className="absolute bottom-5 capitalize left-5 text-white font-bold md:text-[30px] text-[20px]">
        {card.title}
      </div>
    </div>
  );
};

export default function Discover() {
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 4, // Adjust the number of slides to show
    slidesToScroll: 1,
  };

  return (
    <section className="my-14">
      <Container>
        <div>
          <SectionTitle title="discover" />
          <Slider {...settings}>
            {cards.map((card) => (
              <DiscoverCard card={card} key={card.id} />
            ))}
          </Slider>
        </div>
      </Container>
    </section>
  );
}
