import React from 'react';
import { img1 } from "../../assets/home/bg2.png";

const InfoCard = ({ image, heading, info, onClick }) => {
  return (
    <div className="flex items-center p-4 mb-4 bg-gray-300 rounded-md shadow-md">
      <div className="flex-shrink-0 w-24 h-24 overflow-hidden rounded-md mr-4">
        <img src={image} alt="info_image" className="w-full h-full object-cover" />
      </div>
      <div className="flex-grow">
        <h2 className="text-xl font-bold mb-2">{heading}</h2>
        <p className="text-gray-600 mb-4">{info}</p>
        <div className="text-right">
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600 transition"
            onClick={onClick}
          >
            Know more
          </button>
        </div>
      </div>
    </div>
  );
};

export default InfoCard;
