import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { logo } from "../assets/home";
import Container from "./Container";

function MobileNavLinks({ columnLinks }) {
  return (
    <div className="flex flex-col items-center mt-4">
      {columnLinks.map((column, index) => (
        <Link
          key={index}
          to={column.link}
          className="text-white py-2 hover:bg-[#A50014] hover:text-white"
        >
          {column.name}
        </Link>
      ))}
    </div>
  );
}

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobileView, setIsMobileView] = useState(false);
  const [notifications, setNotifications] = useState([
    "This website is hacked by Yamini",
    "Vishnu wants to sit in IT2",
    "BML sir loves Bhandari",
  ]);

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 768);
    };

    handleResize();
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const columnLinks = [
    { name: "First", link: "/first-link" },
    { name: "Second", link: "/second-link" },
    { name: "Third", link: "/third-link" },
    { name: "Fourth", link: "/fourth-link" },
    { name: "Fifth", link: "/fifth-link" },
    { name: "Sixth", link: "/sixth-link" },
    { name: "Seventh", link: "/seventh-link" },
    { name: "Eighth", link: "/eighth-link" },
  ];

  return (
    <div>
      {/* Notifications */}
      {/* ... (No changes here) */}

      {/* Navbar */}
      <nav className="py-5 z-40 relative">
        <Container>
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center gap-4">
              <img
                className="h-[96px] w-[370px]"
                src={logo}
                alt="Workflow"
              />
              {isMobileView ? (
                <button
                  onClick={() => setIsOpen(!isOpen)}
                  className="md:hidden text-white focus:outline-none"
                >
                  {isOpen ? "Close" : "Menu"}
                </button>
              ) : (
                <div className="hidden md:block">
                  {/* Additional content if needed */}
                </div>
              )}
            </div>

            <div className="flex gap-4 items-center">
              <Link to="/">
                <div className="hidden md:block hover:bg-[#A50014] hover:text-white px-4 py-1 rounded-md">
                  Login
                </div>
              </Link>
              <Link to="/">
                <div className="hidden md:block hover:bg-[#A50014] hover:text-white px-4 py-1 rounded-md">
                  SignUp
                </div>
              </Link>
            </div>
          </div>

          {isMobileView && isOpen && (
            <MobileNavLinks columnLinks={columnLinks} />
          )}
        </Container>
      </nav>

      {/* Red color bar with centered columns separated by a white line */}
      {isMobileView ? null : (
        <div className="bg-[#A50014] h-10 flex items-center text-white">
          {columnLinks.map((column, index) => (
            <React.Fragment key={index}>
              {index > 0 && <div className="h-full w-0.5 bg-white"></div>}
              <Link to={column.link} className="flex-1 text-center">
                {column.name}
              </Link>
            </React.Fragment>
          ))}
        </div>
      )}
    </div>
  );
}

export default Navbar;
