import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { logo } from "../assets/home";
import Container from "./Container";

function MobileNavLinks({ columnLinks }) {
  return (
    <div className="bg-red-500 py-2 text-white mt-4"> {/* Added mt-4 for top margin */}
      {columnLinks.map((column, index) => (
        <Link
          key={index}
          to={column.link}
          className="block py-2 px-4 hover:bg-[#A50014] hover:text-white"
        >
          {column.name}
        </Link>
      ))}
      {/* Add login and signup buttons in the dropdown list */}
      <Link to="/login" className="block py-2 px-4 hover:bg-[#A50014] hover:text-white">
        Login
      </Link>
      <Link to="/signup" className="block py-2 px-4 hover:bg-[#A50014] hover:text-white">
        Signup
      </Link>
    </div>
  );
}


function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobileView, setIsMobileView] = useState(false);
  const [notifications, setNotifications] = useState([
    "This website is hacked by Yamini",
    "Vishnu wants to sit in IT2",
    "BML sir loves Bhandari",
  ]);

  useEffect(() => {
    const handleResize = () => {
      setIsMobileView(window.innerWidth <= 768);
    };

    handleResize();
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const columnLinks = [
    { name: "First", link: "/first-link" },
    { name: "Second", link: "/second-link" },
    { name: "Third", link: "/third-link" },
    { name: "Fourth", link: "/fourth-link" },
    { name: "Fifth", link: "/fifth-link" },
    { name: "Sixth", link: "/sixth-link" },
    { name: "Seventh", link: "/seventh-link" },
    { name: "Eighth", link: "/eighth-link" },
  ];

  return (
    <div>
      {/* Notifications */}
      <div className="bg-[#A50014] text-white py-2 text-center overflow-hidden">
        <div className="flex items-center justify-start">
          <div className="flex items-center pr-4">
            <span className="text-sm mx-5 font-bold">ANNOUNCEMENTS</span>
          </div>
          <marquee>
            {notifications.map((notification, index) => (
              <span key={index}>{notification} | </span>
            ))}
          </marquee>
        </div>
      </div>

      {/* Navbar */}
      <nav className="py-5 z-40 relative">
        <Container>
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center gap-4">
              <img
                className="h-[96px] w-[370px]"
                src={logo}
                alt="Workflow"
              />
              {isMobileView ? (
                <button
                  onClick={() => setIsOpen(!isOpen)}
                  className="md:hidden text-red-500 focus:outline-none"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                    className="h-6 w-6"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d="M4 6h16M4 12h16m-7 6h7"
                    />
                  </svg>
                </button>
              ) : (
                <div className="hidden md:block">
                  {/* Additional content if needed */}
                </div>
              )}
            </div>

            <div className="flex gap-4 items-center">
              <Link to="/">
                <div className="hidden md:block hover:bg-[#A50014] hover:text-white px-4 py-1 rounded-md">
                  Login
                </div>
              </Link>
              <Link to="/">
                <div className="hidden md:block hover:bg-[#A50014] hover:text-white px-4 py-1 rounded-md">
                  SignUp
                </div>
              </Link>
            </div>
          </div>

          {isMobileView && isOpen && <MobileNavLinks columnLinks={columnLinks} />}
        </Container>
      </nav>

      {/* Red color bar with centered columns separated by a white line */}
      {isMobileView ? null : (
        <div className="bg-[#A50014] h-10 flex items-center text-white">
          {columnLinks.map((column, index) => (
            <React.Fragment key={index}>
              {index > 0 && <div className="h-full w-0.5 bg-white"></div>}
              <Link to={column.link} className="flex-1 text-center">
                {column.name}
              </Link>
            </React.Fragment>
          ))}
        </div>
      )}
    </div>
  );
}

export default Navbar;
